({ deep, require }) => {
  const React = require('react');
  const { useState } = require('react');
  const axiosHooks = require("axios-hooks");
  const axios = require("axios");
  const useAxios = axiosHooks.makeUseAxios({ axios: axios.create() });

  return ({ fillSize, style, link }) => {

    const ssl = deep.apolloClient.ssl;
    const path = deep.apolloClient.path.slice(0, -4);
    const url = `${ssl ? "https://" : "http://"}${path}/file?linkId=${link.id}`;

    const [{ data, loading, error }, refetch] = useAxios({ 
      method: 'get',
      url,
      headers: {
        'Authorization': `Bearer ${deep.token}`
      },

      responseType: "blob",
    });

    const [src, setSrc] = useState("test");
    if (!loading && data) {
      const reader = new window.FileReader();
      reader.onload = () => {
        setSrc(reader.result);
      }
      reader.readAsDataURL(data);
    }

    const renderMedia = () => {
      if (src.startsWith('data:image')) {
        return <img src={src} alt="An image from Deep file storage." style={style}></img>;
      } else if (src.startsWith('data:audio')) {
        return <audio src={src} controls style={style}>Your browser does not support the audio element.</audio>;
      } else {
        return <div>Unsupported file type.</div>;
      }
    } 

    return renderMedia();
  }
}

({ deep, require }) => {
      const React = require('react');
    const { useState, useEffect } = React;
    const json5 = require('json5');
    const { Input, useColorModeValue } = require('@chakra-ui/react');
    const { useHotkeys } = require('react-hotkeys-hook');
    const { useDebounceCallback } = require('@react-hook/debounce');
    const { useContainer } = require('@deep-foundation/deepcase');
    // Only string editor.
    return ({ fillSize, style, link, onClose }) => {


      const currentValue = deep.stringify(link?.value?.value) || '';
      const [value, setValue] = useState(currentValue);
      const save = async (value) => {
          if (!link.value) await deep.insert({
            link_id: link.id, value: value,
        }, { table: 'strings' });
        deep.update({ link_id: link.id }, { value: value }, { table: 'strings' });

      };
      const ref = useHotkeys('enter', async () => {
        await save(value);
        onClose && onClose();
      }, { enableOnTags: ["INPUT"] });

      const setValuesDebounced = useDebounceCallback(async(value) => {await save(value)}, 500);

      useEffect(() => {
        setValuesDebounced(value);
      }, [value])

      const bg = useColorModeValue('#eeeeee', '#434343');
      const hover = useColorModeValue('white', '#1e1e1e');
      const borderColor = useColorModeValue('#434343', '#eeeeee');
      const color = useColorModeValue('#1e1e1e', 'white');

    return <div
        style={{
          width: 300,
          ...style,
          position: 'relative', 
          // background: 'red'
        }}
      >
        <Input
          ref={ref}
          autoFocus
          type="text"
          variant='filled'
          variant='filled'
          bg={bg}
          color={color}
          borderWidth='thin'
          borderColor={borderColor}
          _hover={{bg: hover}}
          _focus={{bg: bg}}
          sx={{
            width: '100%',
          }}
          onChange={(e) => setValue(e.target.value)}
          value={value}
        />
      </div>;
    }
  }

({ deep, require }) => {
    const React = require('react');
    const { useState, useEffect } = React;
    const json5 = require('json5');
    const { useContainer, Resize, CustomizableIcon } = require('@deep-foundation/deepcase');
    const MonacoEditor = require('@monaco-editor/react');
    const { Box, IconButton, useColorModeValue, useColorMode } = require('@chakra-ui/react');
    const { BsCheck2, BsLightbulbFill, BsLightbulbOff } = require('react-icons/bs');
    const { motion, useAnimation } = require('framer-motion');

    const variants = {
      view: {
        opacity: 1,
        scale: 1,
        transition: {
          duration: 1,
          delay: 0.3,
          scale: { delay: 0.5, } 
        }
      },
      hide: {
        opacity: 0, 
        scale: 0,
        transition: {
          type: 'spring'
        }
      },
      initial: {
        originX: 1,
        opacity: 1,
        scale: 1,
      }
    };

    const stackVariants = {
      outside: {
        x: '0%',
        opacity: 1,
        scale: 1,
        originX: 0,
        transition: {
          type: 'spring',

          duration: 0.5,
          delay: 0.2,
          scale: { delay: 0.3, }
        }
      },
      nested: {
        x: '-100%',
        opacity: 1,
        scale: 1,
        originX: 1,
        transition: {
          type: 'spring',
          duration: 0.5,
          delay: 0.2,
          scale: { delay: 0.3, }
        }
      },
      initial: {
        x: '0%',
        opacity: 0,
        scale: 0,
      }
    };

    // Only objects editor.
    return ({ fillSize, style, link }) => {
      const currentValue = deep.stringify(link?.value?.value) || '';
      const [value, setValue] = useState(currentValue);
      const isSaved = value == currentValue;
      const [container] = useContainer();
      const { data } = deep.useDeepSubscription({
        type_id: { _in: [
          deep.idLocal('@deep-foundation/core', 'Active'),
          deep.idLocal('@deep-foundation/core', 'Contain'),
        ] },
        to_id: { _eq: link.id },
      });
      const contain = data?.find(l => l.type_id === deep.idLocal('@deep-foundation/core', 'Contain'))
      const active = data?.find(l => l.type_id === deep.idLocal('@deep-foundation/core', 'Active'))

      const [viewSize, setViewSize] = useState({width: 300, height: 300});
      const terminalBorderWidth = viewSize.width - 1;
      const terminalBorderHeight = viewSize.height - 1;

      const control = useAnimation();
      const controlStack = useAnimation();

      useEffect(() => {
        if (value) {
          control.start('view')
        } else {
          control.start('hide')
        };
        if (fillSize === true) {
          controlStack.start('nested')
        } else {
          // controlStack.start('initial')
          controlStack.start('outside')
        };

      }, [control, controlStack, value, fillSize])

      const { colorMode } = useColorMode();

      const body = <div
          style={{
            width: viewSize.width,
            height: viewSize.height,
            ...style,
            position: 'relative',
          }}
        >
          <Box 
            position='relative'
            display='grid' 
            gridTemplateColumns='1fr max-content' 
            height='inherit'
          >
            <Box
              position='relative'
              overflow='hidden' sx={{ borderRadius: 5 }}
              border='1px dashed #605c60'
            > 
              <MonacoEditor
                options={{
                  minimap: {
                    enabled: false
                  },
                  lineNumbers: 'off',
                  wordWrap: true,
                }}
                height="100%"
                width="100%"
                theme={colorMode === 'light' ? 'light' : "vs-dark"}
                defaultLanguage="json"
                defaultValue={value || ''}
                onChange={(value) => setValue(value)}
                // onMount={handleEditorDidMount}
              />
            </Box>
            <Box
              as={motion.div}
              animate={controlStack}
              variants={stackVariants}
              initial='initial'
              // height={stackHeight}
              height={fillSize ? '100%' : viewSize.height}
              display='flex'
              justifyContent='space-between'
              flexDirection='column'
              ml={fillSize ? 0 : '0.2rem'}
            >
              <IconButton 
                as={motion.div}
                variants={variants}
                initial='initial'
                // whileInView='view'
                animate='view'
                aria-label='activate/inactivate button' 
                bg={active && colorMode === 'dark' ? 'gray.700' : 'blue.50'}
                _hover={{
                  bg: 'blue.100'
                }}
                isRound
                variant='outline'
                sx={{ borderColor: active ? '#111' : 'rgb(0, 128, 255)' }}
                mr={fillSize ? '0.2rem' : 0}
                mt={fillSize ? '0.2rem' : 0}
                size='xs'
                onClick={async () => {
                    if (active) {
                      await deep.delete({
                        _or: [{
                          id: { _eq: active?.id },
                      }, {
                          type_id: deep.idLocal('@deep-foundation/core', 'Contain'),
                        from_id: link.id,
                        to_id: active.id,
                      }],
                    });
                  } else {
                      await deep.insert({
                        type_id: deep.idLocal('@deep-foundation/core', 'Active'),
                      from_id: contain.from_id,
                      to_id: contain.to_id,
                      in: { data: {
                          type_id: deep.idLocal('@deep-foundation/core', 'Contain'),
                        from_id: link.id,
                      } },
                    });
                  }
                }}
                icon={active ? <BsLightbulbOff /> : <CustomizableIcon Component={BsLightbulbFill} value={{ color: 'rgb(0, 128, 255)' }} />}
              />
              <IconButton 
                as={motion.div}
                variants={variants}
                initial='initial'
                animate={control}
                whileInView='view'
                // isDisabled={isSaved}
                aria-label='save button' 
                isRound
                bg='blue.50'
                _hover={{
                  bg: 'blue.100'
                }}
                variant='outline'
                sx={{ borderColor: 'rgb(0, 128, 255)' }}
                mr={fillSize ? '0.2rem' : 0}
                mb={fillSize ? '0.2rem' : 0}
                // isLoading={!isSaved}
                size='xs'
                icon={<CustomizableIcon Component={BsCheck2} value={{ color: 'rgb(0, 128, 255)' }} />}
                onClick={() => {
                  try {
                    const _value = json5.parse(value);
                    if (!link.value) deep.insert({
                      link_id: link.id, value: _value,
                    }, { table: 'objects' });
                    deep.update({ link_id: link.id }, { value: _value }, { table: 'objects' });
                  } catch(error) {}
                }}
              />
            </Box>
          </Box>
        </div>;

      return  <>
        {fillSize 
        ? body 
        : <Resize
            onChangeSize={(viewSize) => setViewSize(viewSize)} 
            style={{
            // position: 'relative',
            // overflow: nested ? 'hidden' : 'inherit',
            borderRadius: 5,
            border: 'none',
          }}
        >{body}</Resize>}
      </>;
    }
  }

